
string = ""
A = 0
B = 0
C = 0

# 999
for i in range(999):
	string = string + ""

print (string)

for i in range(len(string)):
	if string[i] == 'a':
		for j in range(1, 1001):
			if i - j > 0:
				if string[i - j] == 'A':
					A += 1
		for j in range(1, 1001):
			if i + j < len(string):
				if string[i + j] == 'A':
					A += 1
	if string[i] == 'b':
		for j in range(1, 1001):
			if i - j > 0:
				if string[i - j] == 'B':
					B += 1
		for j in range(1, 1001):
			if i + j < len(string):
				if string[i + j] == 'B':
					B += 1
	if string[i] == 'c':
		for j in range(1, 1001):
			if i - j > 0:
				if string[i - j] == 'C':
					C += 1
		for j in range(1, 1001):
			if i + j < len(string):
				if string[i + j] == 'C':
					C += 1

print ( A + B + C)
